        { "id": 10, "grammar_point": "～ことができません (inability)", "english_sentence": "I cannot drive a car.", "correct_japanese": "車 を 運転 する こと が でき ませ ん。", "word_bank": ["車", "を", "運転", "する", "こと", "が", "でき", "ませ", "ん。"], "hint": "Expressing inability.", "distractors": ["運転できます", "運転します"] }
