        { "id": 2, "grammar_point": "～てばかり (just/only)", "english_sentence": "She has just arrived, so she must be tired.", "correct_japanese": "彼女 は 来 た ばかり な の で 、疲れ て いる はず です。", "word_bank": ["彼女", "は", "来", "た", "ばかり", "な", "ので", "、", "疲れ", "て", "いる", "はず", "です。"], "hint": "Indicates a recent action (similar to たばかり).", "distractors": ["来たところ", "来ている"] },
        { "id": 3, "grammar_point": "～ことになっている (it has been decided)", "english_sentence": "It has been decided that the meeting will be held online next week.", "correct_japanese": "来週 の 会議 は オンライン で 行わ れる こと に なっ て います。", "word_bank": ["来週", "の", "会議", "は", "オンライン", "で", "行わ", "れる", "こと", "に", "なっています。"], "hint": "Indicates a decision made by others or a custom.", "distractors": ["することにした", "する予定だ"] },
        { "id": 4, "grammar_point": "～ことにする (decide to)", "english_sentence": "I decided to quit smoking for my health.", "correct_japanese": "健康 の ため に たばこ を やめる こと に し ました。", "word_bank": ["健康", "の", "ため", "に", "たばこ", "を", "やめる", "こと", "に", "し", "ました。"], "hint": "Indicates a personal decision.", "distractors": ["やめます", "やめたいです"] },
        { "id": 5, "grammar_point": "～ようにする (make an effort to)", "english_sentence": "I try to eat vegetables every day for my health.", "correct_japanese": "健康 の ため に 毎日 野菜 を 食べる よう に し て います。", "word_bank": ["健康", "の", "ため", "に", "毎日", "野菜", "を", "食べる", "よう", "に", "し", "て", "います。"], "hint": "To make an effort to do something regularly.", "distractors": ["食べます", "食べよう"] },
        { "id": 6, "grammar_point": "～ようになる (become able to)", "english_sentence": "After studying hard for a year, I became able to speak Japanese fluently.", "correct_japanese": "1年 間 一生懸命 勉強 し た の で 、日本語 が 流暢 に 話せる よう に なり ました。", "word_bank": ["1年", "間", "一生懸命", "勉強", "し", "た", "ので", "、", "日本語", "が", "流暢", "に", "話せる", "よう", "に", "なり", "ました。"], "hint": "Indicates a change in ability or state.", "distractors": ["話せます", "話します"] },
        { "id": 7, "grammar_point": "～ために (for the purpose of)", "english_sentence": "I study Japanese every day to work in Japan in the future.", "correct_japanese": "将来 日本 で 働く ため に 毎日 日本語 を 勉強 し て います。", "word_bank": ["将来", "日本", "で", "働く", "ため", "に", "毎日", "日本語", "を", "勉強", "し", "て", "います。"], "hint": "Expressing purpose.", "distractors": ["ので", "から"] },
        { "id": 8, "grammar_point": "～のに (even though)", "english_sentence": "Even though he studied very hard, he couldn't pass the difficult exam.", "correct_japanese": "彼 は とても 一生懸命 勉強 し た の に 、難しい 試験 に 合格 でき ませ ん でした。", "word_bank": ["彼", "は", "とても", "一生懸命", "勉強", "し", "た", "のに", "、", "難しい", "試験", "に", "合格", "でき", "ません", "でした。"], "hint": "Expressing an unexpected outcome or contrast.", "distractors": ["から", "ので"] },
        { "id": 9, "grammar_point": "～からこそ (precisely because)", "english_sentence": "Precisely because it's difficult, I want to challenge it.", "correct_japanese": "難しい から こそ 、挑戦 し たい です。", "word_bank": ["難しい", "から", "こそ", "、", "挑戦", "し", "たい", "です。"], "hint": "Emphasizes the reason.", "distractors": ["難しいから", "難しいので"] },
        { "id": 10, "grammar_point": "～さえ～ば (if only... then)", "english_sentence": "If only I had money, I could buy that expensive car.", "correct_japanese": "お金 さえ あれ ば 、あの 高い 車 が 買え ます。", "word_bank": ["お金", "さえ", "あれ", "ば", "、", "あの", "高い", "車", "が", "買え", "ます。"], "hint": "Expressing a condition for something to happen.", "distractors": ["お金があれば", "お金があったら"] }
