        { "id": 2, "grammar_point": "～に違いない (strong certainty)", "english_sentence": "The lights are on and I hear voices, so someone must be home.", "correct_japanese": "電気 が つい て い て 声 が 聞こえる から 、誰 か 家 に いる に 違い ない。", "word_bank": ["電気", "が", "つい", "て", "い", "て", "声", "が", "聞こえる", "から", "、", "誰か", "家", "に", "いる", "に", "違い", "ない。"], "hint": "Strong certainty based on clear evidence.", "distractors": ["いるでしょう", "いるはずだ"] },
        { "id": 3, "grammar_point": "～に決まっている (strong certainty, informal)", "english_sentence": "If you don't study at all, you'll definitely fail the exam.", "correct_japanese": "全然 勉強 し ない ん だ から 、試験 に 落ちる に 決まっ て いる。", "word_bank": ["全然", "勉強", "し", "ない", "ん", "だ", "から", "、", "試験", "に", "落ちる", "に", "決まっている。"], "hint": "Strong certainty, informal and often with a sense of inevitability.", "distractors": ["落ちるでしょう", "落ちるはずだ"] },
        { "id": 4, "grammar_point": "～はずだった (was supposed to)", "english_sentence": "I was supposed to go to the concert yesterday, but I got sick and couldn't go.", "correct_japanese": "昨日 コンサート に 行く はず だっ た が 、病気 に なっ て 行け なかっ た。", "word_bank": ["昨日", "コンサート", "に", "行く", "はず", "だった", "が", "、", "病気", "に", "なっ", "て", "行け", "なかっ", "た。"], "hint": "Expressing something that was planned or expected but didn't happen.", "distractors": ["行く予定だった", "行きたかった"] },
        { "id": 5, "grammar_point": "～はずではなかった (was not supposed to)", "english_sentence": "The meeting was not supposed to be this long, but it ended up being over 3 hours.", "correct_japanese": "会議 は こんな に 長く 続く はず で は なかっ た の に 、3時間 以上 も かかっ て しまい ました。", "word_bank": ["会議", "は", "こんな", "に", "長く", "続く", "はず", "で", "は", "なかっ", "た", "の", "に", "、", "3時間", "以上", "も", "かかっ", "て", "しまい", "ました。"], "hint": "Expressing something that was not planned or expected to happen.", "distractors": ["続く予定ではなかった", "続かなかった"] },
        { "id": 6, "grammar_point": "～ことになっている (it is expected/customary)", "english_sentence": "In Japan, it is customary to take off your shoes when entering a house.", "correct_japanese": "日本 で は 、家 に 入る 時 は 靴 を 脱ぐ こと に なっ て います。", "word_bank": ["日本", "で", "は", "、", "家", "に", "入る", "時", "は", "靴", "を", "脱ぐ", "こと", "に", "なっています。"], "hint": "Indicates a custom, rule, or general understanding.", "distractors": ["脱ぐべきだ", "脱ぐことにした"] },
        { "id": 7, "grammar_point": "～ことになっている (rule/regulation)", "english_sentence": "According to the school rules, students are not allowed to use their smartphones during class.", "correct_japanese": "校則 で 、授業中 は スマートフォン を 使っ て は いけない こと に なっ て います。", "word_bank": ["校則", "で", "、", "授業中", "は", "スマートフォン", "を", "使って", "は", "いけない", "こと", "に", "なっています。"], "hint": "Indicates a formal rule or regulation.", "distractors": ["使うべきではない", "使わないほうがいい"] },
        { "id": 8, "grammar_point": "～ことになっている (external decision)", "english_sentence": "The new product will be released next month.", "correct_japanese": "新製品 は 来月 発売 さ れる こと に なっ て います。", "word_bank": ["新製品", "は", "来月", "発売", "さ", "れる", "こと", "に", "なっています。"], "hint": "Indicates a decision made by an organization or external party.", "distractors": ["発売する予定だ", "発売するはずだ"] },
        { "id": 9, "grammar_point": "～ことにする (personal decision)", "english_sentence": "Since I'm going to Japan, I decided to start studying Japanese from today.", "correct_japanese": "日本 へ 行く の で 、今日 から 日本語 を 勉強 する こと に し ました。", "word_bank": ["日本", "へ", "行く", "ので", "、", "今日", "から", "日本語", "を", "勉強", "する", "こと", "に", "し", "ました。"], "hint": "Indicates a personal decision or resolution.", "distractors": ["勉強します", "勉強したいです"] },
        { "id": 10, "grammar_point": "～ことにしている (make it a habit to)", "english_sentence": "I make it a habit to check my email every morning before starting work.", "correct_japanese": "仕事 が 始まる 前 に 、毎朝 メール を チェック する こと に し て います。", "word_bank": ["仕事", "が", "始まる", "前", "に", "、", "毎朝", "メール", "を", "チェック", "する", "こと", "に", "し", "て", "います。"], "hint": "Indicates a personal habit or routine.", "distractors": ["チェックします", "チェックするべきだ"] }
