        { "id": 7, "grammar_point": "～ほど～ない", "english_sentence": "This car is not as expensive as that car.", "correct_japanese": "この車はあの車ほど高くありません。", "word_bank": ["この", "車", "は", "あの", "車", "ほど", "高く", "あり", "ません。"], "hint": "Comparison of price.", "distractors": ["より", "安い"] },
        { "id": 8, "grammar_point": "～の中で一番", "english_sentence": "Among cities, I like Kyoto the most.", "correct_japanese": "都市の中で京都が一番好きです。", "word_bank": ["都市", "の", "中", "で", "京都", "が", "一番", "好き", "です。"], "hint": "Favorite among cities.", "distractors": ["東京", "大阪"] },
        { "id": 9, "grammar_point": "～と同じ", "english_sentence": "My height is the same as my father's.", "correct_japanese": "私の背は父と同じです。", "word_bank": ["私", "の", "背", "は", "父", "と", "同じ", "です。"], "hint": "Comparison of height.", "distractors": ["違う", "より"] },
        { "id": 10, "grammar_point": "～と違う", "english_sentence": "This color is different from that color.", "correct_japanese": "この色はあの色と違います。", "word_bank": ["この", "色", "は", "あの", "色", "と", "違い", "ます。"], "hint": "Comparison of colors.", "distractors": ["同じ", "より"] }
