        { "id": 2, "grammar_point": "～はずです", "english_sentence": "He should be here by now, as he left early.", "correct_japanese": "彼は早く出たので、もうここにいるはずです。", "word_bank": ["彼", "は", "早く", "出た", "ので", "、", "もう", "ここ", "に", "いる", "はず", "です。"], "hint": "Expressing expectation or likelihood based on a reason.", "distractors": ["いるでしょう", "います"] },
        { "id": 3, "grammar_point": "～はずがない", "english_sentence": "It's impossible that he doesn't know the answer to such an easy question.", "correct_japanese": "こんな簡単な質問の答えを彼が知らないはずがない。", "word_bank": ["こんな", "簡単", "な", "質問", "の", "答え", "を", "彼", "が", "知ら", "ない", "はず", "が", "ない。"], "hint": "Expressing impossibility or unlikelihood.", "distractors": ["知っているでしょう", "知っています"] },
        { "id": 4, "grammar_point": "～に違いない", "english_sentence": "He must be tired because he worked all night.", "correct_japanese": "彼は徹夜したので、疲れているに違いない。", "word_bank": ["彼", "は", "徹夜", "し", "た", "ので", "、", "疲れ", "て", "いる", "に", "違い", "ない。"], "hint": "Expressing strong certainty.", "distractors": ["疲れているでしょう", "疲れているはずだ"] },
        { "id": 5, "grammar_point": "～に決まっている", "english_sentence": "Of course, he will pass the exam; he studies every day.", "correct_japanese": "毎日勉強しているから、彼は試験に合格するに決まっている。", "word_bank": ["毎日", "勉強", "し", "て", "いる", "から", "、", "彼", "は", "試験", "に", "合格", "する", "に", "決まっている。"], "hint": "Expressing strong certainty (informal).", "distractors": ["合格するでしょう", "合格するはずだ"] },
        { "id": 6, "grammar_point": "～ようとする", "english_sentence": "The baby is trying to walk for the first time.", "correct_japanese": "赤ちゃんが初めて歩こうとしています。", "word_bank": ["赤ちゃん", "が", "初めて", "歩こう", "と", "し", "て", "います。"], "hint": "To try to do something (often with effort or intention).", "distractors": ["歩けます", "歩きます"] },
        { "id": 7, "grammar_point": "～ようとしない", "english_sentence": "He doesn't try to understand other people's feelings at all.", "correct_japanese": "彼は全然人の気持ちを理解しようとしない。", "word_bank": ["彼", "は", "全然", "人", "の", "気持ち", "を", "理解", "し", "よう", "と", "し", "ない。"], "hint": "To not try to do something.", "distractors": ["理解しない", "理解できない"] },
        { "id": 8, "grammar_point": "～ば～ほど", "english_sentence": "The more you read this book, the more interesting it becomes.", "correct_japanese": "この本は読めば読むほど面白くなります。", "word_bank": ["この", "本", "は", "読め", "ば", "読む", "ほど", "面白く", "なり", "ます。"], "hint": "The more... the more...", "distractors": ["読んだら面白い", "読むと面白い"] },
        { "id": 9, "grammar_point": "～ばかり (only/just)", "english_sentence": "He only plays games every day and doesn't study at all.", "correct_japanese": "彼は毎日ゲームばかりしていて、全然勉強しません。", "word_bank": ["彼", "は", "毎日", "ゲーム", "ばかり", "し", "て", "い", "て", "、", "全然", "勉強", "し", "ません。"], "hint": "Indicates doing only one thing (often with a negative nuance).", "distractors": ["ゲームもする", "ゲームだけする"] },
        { "id": 10, "grammar_point": "～ばかりでなく (not only... but also)", "english_sentence": "He is not only good at sports but also good at studying.", "correct_japanese": "彼はスポーツが得意なばかりでなく、勉強もよくできます。", "word_bank": ["彼", "は", "スポーツ", "が", "得意", "な", "ばかり", "で", "なく", "、", "勉強", "も", "よく", "でき", "ます。"], "hint": "Not only A, but also B.", "distractors": ["スポーツも勉強も", "スポーツだけ"] }
