        { "id": 7, "grammar_point": "～につれて (as X changes, Y also changes)", "english_sentence": "As the season changes, the scenery also changes.", "correct_japanese": "季節が移るにつれて、景色も変わっていきます。", "word_bank": ["季節", "が", "移る", "に", "つれ", "て", "、", "景色", "も", "変わっ", "て", "いき", "ます。"], "hint": "As X changes, Y also changes proportionally.", "distractors": ["移ると", "移るにともなって"] },
        { "id": 8, "grammar_point": "～にわたって (range, extent)", "english_sentence": "The discussion lasted for three hours.", "correct_japanese": "議論は3時間にわたって行われました。", "word_bank": ["議論", "は", "3時間", "に", "わたって", "行わ", "れ", "ました。"], "hint": "Indicates something extending over a period or range.", "distractors": ["3時間で", "3時間の間"] },
        { "id": 9, "grammar_point": "～に際して (when/on the occasion of)", "english_sentence": "When starting a new business, it is important to have a solid plan.", "correct_japanese": "新しい事業を始めるに際しては、しっかりとした計画が重要です。", "word_bank": ["新しい", "事業", "を", "始める", "に", "際し", "て", "は", "、", "しっかり", "と", "し", "た", "計画", "が", "重要", "です。"], "hint": "At the time of/on the occasion of (formal).", "distractors": ["始める時", "始める前に"] },
        { "id": 10, "grammar_point": "～にともなって (due to/along with)", "english_sentence": "Along with the development of technology, our lives have become more convenient.", "correct_japanese": "技術の発展にともなって、私たちの生活はより便利になりました。", "word_bank": ["技術", "の", "発展", "に", "ともなっ", "て", "、", "私たち", "の", "生活", "は", "より", "便利", "に", "なり", "ました。"], "hint": "As X occurs, Y also occurs (often cause-effect).", "distractors": ["発展すると", "発展につれて"] }
